import React, {Component } from 'react';
import Intro from '../../components/Intro';
import SeriesList from '../../components/SeriesList';
import Loader from '../../components/Loader';
class Series extends Component{
    state = {
        series:[],
        seriesName: '',
        isFetching: false
    }

onSeriesInputChange = e => {
     this.setState({ seriesName:e.target.value, isFetching: true});

 }
    render(){
        const {series,seriesName,isFetching} = this.state;
        return( 
            <div>
            <Intro message="Here you can find all of your most series" /> 
               <div>
                 <input value={seriesName} type= "text" onChange= {this.onSeriesInputChange} />
               </div>
               {
                 !isFetching && series.length ===0 && seriesName.trim() === '' && <p> Please type something </p>
               }
               {
                 !isFetching && series.length === 0 && seriesName.trim() !== '' &&  <p> No tv series found with this name </p>
               }
               {
                   isFetching && <p><Loader /> </p>
               }
               {
                   !isFetching && <p><SeriesList list={this.state.series} /></p>
               }
            </div>
        )
    }
}

export default Series;
